value = false,
className,
checkedIcon = <></>,
uncheckedIcon = <></>,
onChange,
size = "xs",
}) => {
const [selected, setSelected] = React.useState(value);
const handleChange = (val) => {
};
setSelected(val);
onChange?. (val);
return (
<div className={className}>
<SwitchProvider
checked={selected}
onChange={handleChange} {...sizes[size]}
checkedIcon={checkedIcon}
uncheckedIcon={uncheckedIcon}
/> </div> 
);
};
Switch.propTypes = { value: PropTypes.bool, className: PropTypes.string, checkedIcon: PropTypes.node, uncheckedIcon: PropTypes.node, onChange: PropTypes.func, size: PropTypes.oneOf(["xs"]),
};
